package signer

import (
	"reflect"
	"testing"

	"go.uber.org/zap"

	"github.com/kruftik/jenkins-update-dot-json-resigner/internal/config"
	"github.com/kruftik/jenkins-update-dot-json-resigner/internal/jenkins/types"
)

var (
	logger, _ = zap.NewDevelopment()

	unsigned = &types.InsecureUpdateJSON{
		Plugins: map[string]types.Plugin{
			"text": {
				URL: "http://origin.local/download/plugin.hpi",
			},
		},
		UpdateCenterVersion: "123",
	}

	log = logger.Sugar()
)

func TestValidSignature(t *testing.T) {
	s, err := NewSignerService(log, config.SignerConfig{
		CertificatePath: "../../../testdata/certs/test.crt",
		KeyPath:         "../../../testdata/certs/test.key",
	})
	if err != nil {
		t.Fatal(err)
	}

	validSignature := types.Signature{
		Certificates: []string{
			"",
		},
		CorrectDigest:       "59fd7b4e6de2c7b4859f293b0c0e9c540376c96a",
		CorrectDigest512:    "btNK63Unffc+nLEOEbB9/3OXX2+jr1KdYYfH9Rd+TmYTyDkM05r4JBailigV6KwHmEFLDxiVNzRxIkPY7zGnWw==",
		CorrectSignature:    "",
		CorrectSignature512: "",
	}

	if err := s.VerifySignature(unsigned, validSignature); err != nil {
		t.Fatal(err)
	}

	t.Log("valid signature verified")
}

func TestInvalidSignature(t *testing.T) {
	s, err := NewSignerService(log, config.SignerConfig{
		CertificatePath: "../../../testdata/certs/test.crt",
		KeyPath:         "../../../testdata/certs/test.key",
	})
	if err != nil {
		t.Fatal(err)
	}

	cases := map[string]types.Signature{
		"wrong-CorrectDigest512": {
			Certificates: []string{
				"",
			},
			CorrectDigest:       "59fd7b4e6de2c7b4859f293b0c0e9c540376c96a",
			CorrectDigest512:    "321",
			CorrectSignature:    "",
			CorrectSignature512: "",
		},
		"wrong-CorrectDigest": {
			Certificates: []string{
				"",
			},
			CorrectDigest:       "123",
			CorrectDigest512:    "btNK63Unffc+nLEOEbB9/3OXX2+jr1KdYYfH9Rd+TmYTyDkM05r4JBailigV6KwHmEFLDxiVNzRxIkPY7zGnWw==",
			CorrectSignature:    "",
			CorrectSignature512: "",
		},
		"wrong-CorrectSignature": {
			Certificates: []string{
				"",
			},
			CorrectDigest:       "59fd7b4e6de2c7b4859f293b0c0e9c540376c96a",
			CorrectDigest512:    "btNK63Unffc+nLEOEbB9/3OXX2+jr1KdYYfH9Rd+TmYTyDkM05r4JBailigV6KwHmEFLDxiVNzRxIkPY7zGnWw==",
			CorrectSignature:    "eHh4Cg==",
			CorrectSignature512: "",
		},
		"wrong-CorrectSignature512": {
			Certificates: []string{
				"",
			},
			CorrectDigest:       "59fd7b4e6de2c7b4859f293b0c0e9c540376c96a",
			CorrectDigest512:    "btNK63Unffc+nLEOEbB9/3OXX2+jr1KdYYfH9Rd+TmYTyDkM05r4JBailigV6KwHmEFLDxiVNzRxIkPY7zGnWw==",
			CorrectSignature:    "",
			CorrectSignature512: "xxx",
		},
	}

	for name, test := range cases {
		t.Run(name, func(t *testing.T) {
			if err = s.VerifySignature(unsigned, test); err == nil {
				t.Fatal("invalid signature passed verification")
			}

			t.Logf("invalid signature detected: %v", err)
		})
	}
}

func TestSign(t *testing.T) {
	s, err := NewSignerService(log, config.SignerConfig{
		CertificatePath: "../../../testdata/certs/test.crt",
		KeyPath:         "../../../testdata/certs/test.key",
	})
	if err != nil {
		t.Fatal(err)
	}

	signed := types.SignedUpdateJSON{
		InsecureUpdateJSON: unsigned,
	}

	if err := signed.Sign(s); err != nil {
		t.Fatalf("signing error: %v", err)
	}

	newValidSigrature := types.Signature{
		Certificates: []string{
			"",
		},
		CorrectDigest:       "59fd7b4e6de2c7b4859f293b0c0e9c540376c96a",
		CorrectDigest512:    "btNK63Unffc+nLEOEbB9/3OXX2+jr1KdYYfH9Rd+TmYTyDkM05r4JBailigV6KwHmEFLDxiVNzRxIkPY7zGnWw==",
		CorrectSignature:    "",
		CorrectSignature512: "",
	}

	if !reflect.DeepEqual(signed.Signature, newValidSigrature) {
		t.Fatal("Signatures do not match")
	}

	t.Log("new signature correct")
}
